import React from 'react';

const Header = ({ onNavigation }) => {
  return (
    <header className="bg-gradient-to-r from-purple-600 to-blue-500 shadow-lg py-4 px-6 flex justify-between items-center flex-wrap">
      <div className="text-2xl font-bold text-white w-full md:w-auto text-center md:text-left mb-4 md:mb-0">
        3DTECNOG
      </div>
      <nav className="flex flex-col md:flex-row space-y-2 md:space-y-0 md:space-x-4 w-full md:w-auto items-center">
        {['home', 'productos', 'contacto', 'llaveros'].map(view => (
          <button 
            key={view}
            onClick={() => onNavigation(view)} 
            className="text-white hover:bg-white/20 px-3 py-1 rounded-full transition duration-300 capitalize"
          >
            {view}
          </button>
        ))}
        <button className="bg-white text-purple-600 px-4 py-2 rounded-full hover:bg-gray-100 transition">
          Carrito
        </button>
      </nav>
    </header>
  );
};

export default Header;
