import React, { useState } from 'react';

const LlaveroPersonalizacion = ({ addToCart }) => {
  const [letra, setLetra] = useState('');
  const [colorPrincipal, setColorPrincipal] = useState('#000000');
  const [estiloLetra, setEstiloLetra] = useState('normal');

  const colores = [
    '#000000', '#FFFFFF', '#FF0000', '#00FF00',
    '#0000FF', '#FFFF00', '#FF00FF', '#00FFFF'
  ];

  const estilos = [
    { value: 'normal', label: 'Normal' },
    { value: 'bold', label: 'Negrita' },
    { value: 'italic', label: 'Cursiva' },
    { value: 'underline', label: 'Subrayado' }
  ];

  const handlePersonalizarLlavero = () => {
    if (!letra) {
      alert('Por favor, selecciona una letra');
      return;
    }

    const personalizadoLlavero = {
      id: `personalizado-${Date.now()}`,
      name: `Llavero Personalizado - ${letra}`,
      description: `Llavero con letra ${letra}, color ${colorPrincipal}`,
      price: 50,
      image: null,
      personalizacion: {
        letra,
        colorPrincipal,
        estiloLetra
      }
    };

    addToCart(personalizadoLlavero);
  };

  const getLetraStyle = () => ({
    color: colorPrincipal,
    fontSize: '10rem',
    fontWeight: estiloLetra.includes('bold') ? 'bold' : 'normal',
    fontStyle: estiloLetra.includes('italic') ? 'italic' : 'normal',
    textDecoration: estiloLetra.includes('underline') ? 'underline' : 'none',
    fontFamily: '"Arial Black", Gadget, sans-serif'
  });

  return (
    <div className="grid md:grid-cols-2 gap-8">
      <div className="flex items-center justify-center bg-white rounded-2xl shadow-lg p-8">
        <div className="flex items-center justify-center w-full h-96 bg-white">
          <span style={getLetraStyle()}>
            {letra || 'A'}
          </span>
        </div>
      </div>

      <div>
        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Letra
          </label>
          <input 
            type="text" 
            maxLength="1"
            value={letra}
            onChange={(e) => setLetra(e.target.value.toUpperCase())}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
            placeholder="Introduce una letra"
          />
        </div>

        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Color de la Letra
          </label>
          <div className="flex space-x-2">
            {colores.map(color => (
              <button
                key={color}
                onClick={() => setColorPrincipal(color)}
                style={{ backgroundColor: color }}
                className={`w-8 h-8 rounded-full border ${colorPrincipal === color ? 'ring-2 ring-offset-2 ring-purple-500' : ''}`}
              />
            ))}
          </div>
        </div>

        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Estilo de Letra
          </label>
          <select
            value={estiloLetra}
            onChange={(e) => setEstiloLetra(e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
          >
            {estilos.map(estilo => (
              <option key={estilo.value} value={estilo.value}>
                {estilo.label}
              </option>
            ))}
          </select>
        </div>

        <button
          onClick={handlePersonalizarLlavero}
          className="w-full bg-gradient-to-r from-purple-600 to-pink-500 text-white py-3 rounded-lg hover:opacity-90 transition"
        >
          AÃ±adir Llavero Personalizado
        </button>
      </div>
    </div>
  );
};

export default LlaveroPersonalizacion;
