import React, { useState, useRef, useEffect } from 'react';

const CuadroPersonalizacion = ({ cuadro, onAddToCart, onCancel }) => {
  const imagenRef = useRef(null);
  const [dimensionesImagen, setDimensionesImagen] = useState({ ancho: 0, alto: 0 });

  // Márgenes que se suman a las dimensiones de la imagen
  const margenAncho = 10;
  const margenAlto = 7;

  const [personalizacion, setPersonalizacion] = useState({
    texto: '',
    colorMarco: 'negro',
    tamaño: 'mediano',
  });

  // Usamos ResizeObserver para que se actualice cuando cambia tamaño (responsive)
  useEffect(() => {
    const observer = new ResizeObserver(entries => {
      for (let entry of entries) {
        const { width, height } = entry.contentRect;
        setDimensionesImagen({ ancho: width, alto: height });
      }
    });

    if (imagenRef.current) {
      observer.observe(imagenRef.current);
    }

    return () => {
      if (imagenRef.current) {
        observer.unobserve(imagenRef.current);
      }
    };
  }, [cuadro.overlay]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setPersonalizacion(prev => ({ ...prev, [name]: value }));
  };

  const handleAdd = () => {
    onAddToCart({ ...cuadro, personalizacion });
  };

  // Dimensiones del marco ajustadas a la imagen + margen
  const anchoMarco = dimensionesImagen.ancho + margenAncho;
  const altoMarco = dimensionesImagen.alto + margenAlto;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-2xl shadow-10xl max-w-3xl w-full p-6 relative">
        <button 
          onClick={onCancel}
          className="absolute top-4 right-4 text-gray-500 hover:text-gray-700"
        >
          ✖
        </button>

        <h2 className="text-2xl font-bold mb-4 text-transparent bg-clip-text bg-gradient-to-r from-purple-600 to-blue-500">
          Personalizar {cuadro.name}
        </h2>

        {/* Vista previa con fondo y overlay */}
        <div className="relative flex justify-center items-center w-full mb-6">
          <div
            className="relative"
            style={{
              width: `${anchoMarco}px`,
              height: `${altoMarco}px`,
              maxWidth: '90vw',
              maxHeight: '80vh',
            }}
          >
            {/* Marco iluminado */}
            <img
              src={`${process.env.PUBLIC_URL}/images/cuadros/cuadrosFondoIluminado/rojo.png`}
              alt="Fondo"
              className="absolute top-0 left-0 w-full h-full object-contain z-10 pointer-events-none"
            />

            {/* Imagen overlay */}
            <img
              ref={imagenRef}
              src={cuadro.overlay}
              alt="Overlay del cuadro"
              className="w-full h-full object-contain z-20 pointer-events-none relative"
            />
          </div>
        </div>

        {/* Controles de personalización */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Texto personalizado
            </label>
            <input
              type="text"
              name="texto"
              value={personalizacion.texto}
              onChange={handleChange}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg"
              placeholder="Ej: Para Juan"
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Color del marco
            </label>
            <select
              name="colorMarco"
              value={personalizacion.colorMarco}
              onChange={handleChange}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg"
            >
              <option value="negro">Negro</option>
              <option value="blanco">Blanco</option>
              <option value="madera">Madera</option>
            </select>
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Tamaño
            </label>
            <select
              name="tamaño"
              value={personalizacion.tamaño}
              onChange={handleChange}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg"
            >
              <option value="pequeño">Pequeño</option>
              <option value="mediano">Mediano</option>
              <option value="grande">Grande</option>
            </select>
          </div>
        </div>

        <button
          onClick={handleAdd}
          className="w-full bg-gradient-to-r from-purple-600 to-blue-500 text-white py-3 rounded-lg hover:opacity-90 transition"
        >
          Añadir Cuadro Personalizado al Carrito
        </button>
      </div>
    </div>
  );
};

export default CuadroPersonalizacion;
