import React, { useState } from 'react';
import Header from './components/Header';
import Home from './components/Home';
import Productos from './components/Productos';
import Contacto from './components/Contacto';
import Llaveros from './components/Llaveros';
import Cuadros from './components/Cuadros';
import ProductModal from './components/ProductModal';
import products from './mock/products';


const App = () => {
  const [currentView, setCurrentView] = useState('home');
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [cart, setCart] = useState([]);
  const [showCart, setShowCart] = useState(false);

  const handleNavigation = (view) => {
    setCurrentView(view);
    setSelectedCategory(null);
  };

  const handleCategoryClick = (category) => {
    switch(category) {
      case 'llaveros':
        setCurrentView('llaveros');
        break;
      case 'cuadros':
        setCurrentView('cuadros');
        break;
      case 'cajoneras':
        setCurrentView('cajoneras');
        break;
      default:
        break;
    }
  };

  const handleCloseModal = () => {
    setSelectedCategory(null);
  };
  const addToCart = (product) => {
    const existingProduct = cart.findIndex(item => item.name === product.name);
    if (existingProduct > -1) {
      const updatedCart = [...cart];
      updatedCart[existingProduct].quantity += 1;
      setCart(updatedCart);
    } else {
      setCart([...cart, { ...product, quantity: 1 }]);
    }
    setShowCart(true);
  };

  const removeFromCart = (index) => {
    const updatedCart = cart.filter((_, i) => i !== index);
    setCart(updatedCart);
  };

  const toggleCart = () => {
    setShowCart(!showCart);
  };

  const renderView = () => {
    switch(currentView) {
      case 'home':
        return <Home onCategoryClick={handleCategoryClick} />;
      case 'productos':
        return <Productos />;
      case 'contacto':
        return <Contacto />;
      case 'llaveros':
        return <Llaveros />;  
        case 'cuadros':
          return <Cuadros />;
      default:
        return <Home onCategoryClick={handleCategoryClick} />;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <Header onNavigation={handleNavigation} />
      {renderView()}

      {selectedCategory && (
        <ProductModal 
          category={products[selectedCategory].category}
          products={products[selectedCategory].items}
          onClose={handleCloseModal}
        />
      )}
    </div>
  );
};

export default App;
